def who_is_more_compliant(user):
   from nltk.sentiment import SentimentIntensityAnalyzer
   sia = SentimentIntensityAnalyzer()

   ethan_sentiments = {}
   ben_chavez_sentiments = {}

   for sent in user:
       if "Ethan Davis" in sent:
           words = sent.split()
           pos_words, neg_words = [], []

           for word in words:
               if "ethan" in word or "Ethan" in word:
                   if sia.polarity_scores(word)['compound'] > 0.1:
                       pos_words.append(word)
               elif "Ben" in word or "Benjamin" in word:
                   if sia.polarity_scores(word)['compound'] > 0.1:
                       neg_words.append(word)

           ethan_sentiments[sent] = {'pos': len(pos_words), 'neg': len(neg_words)}
       elif "Benjamin Chavez" in sent:
           words = sent.split()
           pos_words, neg_words = [], []

           for word in words:
               if "ben" in word or "Ben" in word:
                   if sia.polarity_scores(word)['compound'] > 0.1:
                       pos_words.append(word)
               elif "chavez" in word or "Chavez" in word:
                   if sia.polarity_scores(word)['compound'] > 0.1:
                       neg_words.append(word)

           ben_chavez_sentiments[sent] = {'pos': len(pos_words), 'neg': len(neg_words)}

   if ethan_sentiments and ben_chavez_sentiments:
       ethan_score = 0
       ben_score = 0

       for sent, values in ethan_sentiments.items():
           pos = values['pos'] + values['neg']
           neg = values['pos'] - values['neg']

           ethan_score += pos * 0.5 + neg * (-0.1)

       for sent, values in ben_chavez_sentiments.items():
           pos = values['pos'] + values['neg']
           neg = values['pos'] - values['neg']

           ben_score += pos * 0.5 + neg * (-0.1)

       if ethan_score > ben_score:
           return "Ethan Davis"
       elif ben_score > ethan_score:
           return "Benjamin Chavez"
       else:
           return "It is impossible to tell"
   else:
       return "Insufficient data for comparison"

